mapper.select:
select rownum, m_id, m_cdate, m_stop, m_del from 
(select /*+ index_desc(member m.m_id) */
rownum rn, e.e_num, m.m_id, m.m_cdate, m.m_stop, m.m_del from member m LEFT JOIN empl e 
ON m.m_id = e.m_id WHERE

	
	
	
		e_num IS NOT NULL AND
	
	
		e_num IS NULL AND
	


rownum <= #{pageNum} * #{amount}


	AND m.m_id LIKE '%'||#{keyword}||'%'

	)

	where rn > (#{pageNum} - 1) * #{amount}

select e_num, m_id, e_applydate, e_ok from 
(select /*+ index_desc(empl e_num) */
rownum rn, e_num, m_id, e_applydate, e_ok from empl 
WHERE

	
	
	
		e_ok = 'Y' AND
	
	
		e_ok = 'N' AND
	


rownum <= #{pageNum} * #{amount}


	AND m_id LIKE '%'||#{keyword}||'%'

	)

	where rn > (#{pageNum} - 1) * #{amount}
